import React, { Component } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal"; // 모달 사용을 위한 임포트

class CommuPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showWriteForm: false, // 글쓰기 폼 표시 여부
      newPost: {
        title: "",
        author: "",
        content: "",
      },
    };
  }

  // 글쓰기 폼 열기
  handleShowWriteForm = () => {
    this.setState({ showWriteForm: true });
  };

  // 글쓰기 폼 닫기
  handleCloseWriteForm = () => {
    this.setState({ showWriteForm: false });
  };

  // 입력 필드 변경 핸들러
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState((prevState) => ({
      newPost: {
        ...prevState.newPost,
        [name]: value,
      },
    }));
  };

  // 글 작성 완료 핸들러 (데이터 저장 또는 서버 전송 로직 추가 가능)
  handleSubmit = () => {
    console.log("새로운 글:", this.state.newPost);
    // 글쓰기 완료 후 폼 닫기
    this.handleCloseWriteForm();
  };

  render() {
    const { showWriteForm, newPost } = this.state;

    return (
      <div>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>선택</th>
              <th>번호</th>
              <th>제목</th>
              <th>작성자</th>
              <th>작성일</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                <input type="checkbox" />
              </td>
              <td>1</td>
              <td>게시글1</td>
              <td>artistJay</td>
              <td>2022-03-19</td>
            </tr>
            <tr>
              <td>
                <input type="checkbox" />
              </td>
              <td>2</td>
              <td>게시글2</td>
              <td>artistJay</td>
              <td>2022-03-19</td>
            </tr>
            <tr>
              <td>
                <input type="checkbox" />
              </td>
              <td>3</td>
              <td>게시글3</td>
              <td>artistJay</td>
              <td>2022-03-19</td>
            </tr>
          </tbody>
        </Table>

        <Button variant="info" onClick={this.handleShowWriteForm}>
          글쓰기
        </Button>
        <Button variant="secondary">수정하기</Button>
        <Button variant="danger">삭제하기</Button>

        {/* 글쓰기 폼 모달 */}
        <Modal show={showWriteForm} onHide={this.handleCloseWriteForm}>
          <Modal.Header closeButton>
            <Modal.Title>새 글 작성</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formTitle">
                <Form.Label>제목</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="제목을 입력하세요"
                  name="title"
                  value={newPost.title}
                  onChange={this.handleInputChange}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formAuthor">
                <Form.Label>작성자</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="작성자를 입력하세요"
                  name="author"
                  value={newPost.author}
                  onChange={this.handleInputChange}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formContent">
                <Form.Label>내용</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  placeholder="내용을 입력하세요"
                  name="content"
                  value={newPost.content}
                  onChange={this.handleInputChange}
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleCloseWriteForm}>
              취소
            </Button>
            <Button variant="primary" onClick={this.handleSubmit}>
              작성 완료
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default CommuPage;
