import React, { useState } from 'react';

const CommuPage = () => {
  const [selectedBoard, setSelectedBoard] = useState('전체게시판');
  const [title, setTitle] = useState(''); //제목 상태
  const [content, setContent] = useState(''); //내용 상태
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  
  //게시판 변경 핸들러
  const handleBoardChange = (board) => {
    setSelectedBoard(board);
    setTitle(''); //제목 초기화
    setContent(''); //내용 초기화
    setComments([]);
  };

  // 댓글 추가 핸들러
  const handlePostComment = () => {
    if (newComment.trim()) {
      setComments([...comments, newComment]);
      setNewComment('');
    }
  };

  return (
    <div className="community">
      {/* 게시판 선택 */}
      <h1>게시판</h1>
      <div className="tabs">
        <button onClick={()=> handleBoardChange('전체게시판')}>전체게시판</button>
        <button onClick={()=> handleBoardChange('자유게시판')}>자유게시판</button>
        <button onClick={()=> handleBoardChange('고민게시판')}>고민게시판</button>
      </div>

      {/* 선택된 게시판에 따른 내용 */}
      <h2>{selectedBoard}</h2>
      {selectedBoard == '전체게시판' && <p> 전체 게시판의 내용을 표시합니다.</p>}
      {selectedBoard == '자유게시판' && <p> 자유 게시판의 내용을 표시합니다.</p>}
      {selectedBoard == '고민게시판' && <p> 고민 게시판의 내용을 표시합니다.</p>}

      {/* 게시물 작성 */}
      <div className="post">
        <h2>제목</h2>
        <input 
          type="title" 
          placeholder="제목을 입력하세요" 
          value={content} 
          onChange={(e) => setTitle(e.target.value)} 
        />

        <h3>내용</h3>
        <textarea 
          placeholder="내용을 입력하세요" 
          value={content} 
          onChange={(e) => setContent(e.target.value)} 
        />

        <button onClick={() => alert('게시물 등록')}>등록</button>
      </div>

      {/* 댓글 작성 */}
      <div className="comments-section">
        <h3>댓글</h3>
        <textarea 
          placeholder="댓글을 입력하세요" 
          value={newComment} 
          onChange={(e) => setNewComment(e.target.value)} 
        />
        <button onClick={handlePostComment}>댓글 달기</button>

        {/* 댓글 목록 */}
        <div className="comments-list">
          {comments.map((comment, index) => (
            <div key={index} className="comment">
              {comment}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CommuPage;
