import React, { Component } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";

class CommuPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showWriteForm: false, // 글쓰기 폼 표시 여부
      posts: [], // 게시물 목록
      newPost: {
        title: "",
        author: "",
        content: "",
      },
    };
  }

  // 글쓰기 폼 열기
  handleShowWriteForm = () => {
    this.setState({ showWriteForm: true });
  };

  // 글쓰기 폼 닫기
  handleCloseWriteForm = () => {
    this.setState({ 
      showWriteForm: false, 
      newPost: { title: "", author: "", content: "" } // 폼 초기화
    });
  };

  // 입력 필드 변경 핸들러
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState((prevState) => ({
      newPost: {
        ...prevState.newPost,
        [name]: value,
      },
    }));
  };

  // 글 작성 완료 핸들러
  handleSubmit = () => {
    const { newPost, posts } = this.state;

    // 새 게시물 추가
    const newPostWithId = { ...newPost, id: posts.length + 1, date: new Date().toISOString().split("T")[0] };

    this.setState((prevState) => ({
      posts: [...prevState.posts, newPostWithId], // 기존 목록에 새 게시물 추가
      showWriteForm: false, // 모달 닫기
      newPost: { title: "", author: "", content: "" }, // 폼 초기화
    }));
  };

  render() {
    const { showWriteForm, newPost, posts } = this.state;

    return (
      <div>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>선택</th>
              <th>번호</th>
              <th>제목</th>
              <th>작성자</th>
              <th>작성일</th>
            </tr>
          </thead>
          <tbody>
            {posts.length > 0 ? (
              posts.map((post) => (
                <tr key={post.id}>
                  <td>
                    <input type="checkbox" />
                  </td>
                  <td>{post.id}</td>
                  <td>{post.title}</td>
                  <td>{post.author}</td>
                  <td>{post.date}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center">
                  게시물이 없습니다.
                </td>
              </tr>
            )}
          </tbody>
        </Table>

        <Button variant="info" onClick={this.handleShowWriteForm}>
          글쓰기
        </Button>
        <Button variant="secondary">수정하기</Button>
        <Button variant="danger">삭제하기</Button>

        {/* 글쓰기 폼 모달 */}
        <Modal show={showWriteForm} onHide={this.handleCloseWriteForm}>
          <Modal.Header closeButton>
            <Modal.Title>새 글 작성</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formTitle">
                <Form.Label>제목</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="제목을 입력하세요"
                  name="title"
                  value={newPost.title}
                  onChange={this.handleInputChange}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formAuthor">
                <Form.Label>작성자</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="작성자를 입력하세요"
                  name="author"
                  value={newPost.author}
                  onChange={this.handleInputChange}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formContent">
                <Form.Label>내용</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  placeholder="내용을 입력하세요"
                  name="content"
                  value={newPost.content}
                  onChange={this.handleInputChange}
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleCloseWriteForm}>
              취소
            </Button>
            <Button variant="primary" onClick={this.handleSubmit}>
              작성 완료
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default CommuPage;
